import React, { useState } from 'react'
import ReactDOM from 'react-dom'
import './index.css'

const Header = ({course}) => {
  return (
    <h1>{course}</h1>
  );
}

const Part = ({name, exercise}) => (
  <p>
    {name} {exercise}
  </p>
)

const Content = ({parts}) => {
  return (
    <div>
      {parts}
    </div>
  );
}

const Total = ({parts}) => (
  <div>
    <p>Number of exercises {parts.reduce((acc, part) => acc + part.exercises,0)}</p>
  </div>
);

const Button = ({id, handleClick, text}) => (
  <button className="button" id={id} onClick={handleClick}>
    {text}
  </button>
)

const Display = ({counter, allClicks}) => {
  let clicks = "none";
  return (
    <div>
      <h3>Counter: {counter}</h3>
      <p>All clicks made: {allClicks.length > 0 ? allClicks : clicks}</p>
    </div>
  );
}

const Wrapper = ({className, childs}) => {
  return (
    <div className={className}>
      {childs}
    </div>
  );
}

const App = () => {
  const [counter, setCounter] = useState(0);
  const [allClicks, setAll] = useState([]);

  const course = 'Half Stack application development';
  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]

  const incr = () => {
    setAll(allClicks.concat('inc '));
    setCounter(counter + 1);
  }
  const decr = () => {
    setAll(allClicks.concat('decr '));
    setCounter(counter > 0 ? counter - 1 : 0);
  }
  const setzero = () => {
    setAll([]);
    setCounter(0);
  }

  return (
    <div>
      <Header course={course}/>
      <Display counter={counter} allClicks={allClicks}/>
      <Content parts={parts}/>
      <Total total={exercises1 + exercises2 + exercises3}/>
      <Wrapper className={wrapper}>
        <Button 
          handleClick={incr}
          id={"incr"}
          text={"increment counter"}
        />
        <Button 
          handleClick={decr}
          text={"decrement counter"}
        />
        <Button 
          handleClick={setzero}
          text={"set to zero"}
        />
      </Wrapper>
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'));
